import { DestroyService } from './../../services/destroy.service';
import { Subject, takeUntil } from 'rxjs';
import { ChangeDetectionStrategy, Component, OnInit } from '@angular/core';
import { CurrencyService, LatestData } from './currency.service';

@Component({
  selector: 'app-currency',
  templateUrl: './currency.component.html',
  styleUrls: ['./currency.component.scss'],
  changeDetection: ChangeDetectionStrategy.OnPush,
  providers: [CurrencyService, DestroyService],
})
export class CurrencyComponent implements OnInit {
  res = new Subject<LatestData>();

  constructor(public currencySrv: CurrencyService, private readonly destroy$: DestroyService) {}

  ngOnInit() {
    this.currencySrv
      .getData('USD')
      .pipe(takeUntil(this.destroy$))
      .subscribe(data => {
        this.res.next(data);
        console.log(data);
      });
  }
}
<div class="block">
  <h2>Валюты</h2>
  <div class="currency">
    <div>
      <h4>Валюта</h4>
      <p>USD</p>
      <p class="sub-title">Доллар США</p>
      <p>EUR</p>
      <p class="sub-title">Евро</p>
    </div>
    <div>
      <h4>Купить</h4>
      <p> {{ (res | async)?.rates }}</p>
      <p></p>
      <p></p>
      <p></p>
    </div>

    <div>
      <h4>Продать</h4>
    </div>
  </div>
</div>
